mapper.select:
select id,product_name productName,color,price,repertory,category_id categoryId,category_id2
categoryId2,brand,capacity,description,create_time createTime,
picture,status,sale_count saleCount from product  where status='1' and

    category_id=#{categoryId}


    category_id2=#{categoryId2}

ORDER by create_time desc

select id,product_name productName,color,price,brand,repertory, category_id categoryId, category_id2 categoryId2,description,brand,capacity,create_time createTime, picture,status,sale_count saleCount from product
and product_name =#{productName}
and brand=#{brand}
and description like concat ('%',#{description},'%')
and status='1'
ORDER by create_time desc
